package com.tns.java;

public class Nestedloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1; i<=20; i++)
		{
			for(int j=1; j<=20; j++)
			{
	System.out.println("Value of i:"+i+" and value of j:"+j);
			}
			System.out.println("************************");
		}

	}

}
